##### linear regressions by categories #####
#plotNormalHistogram(layer.var$recycling.intensity.cv)
layer.var$blank_prob = factor(layer.var$blank_prob, levels = c(0.5, 0.25, 0.75))
layer.var$scavenge_prob = factor(layer.var$scavenge_prob, levels = c(0.5, 0.25, 0.75))

fit1 = lm(recycling.intensity.cv ~ as.factor(overlap) + as.factor(max_use_intensity) + as.factor(max_artifact_carry) +
            as.factor(max_flake_size) + as.factor(blank_prob) + as.factor(scavenge_prob) + as.factor(mu) + as.factor(num_agents) + 
            as.factor(size_preference) + as.factor(flake_preference) + as.factor(strict_selection) + as.factor(min_suitable_flake_size), 
          data = layer.var)
ri = as.data.frame(summary(fit1)$coefficients) 
ri = ri %>%
  mutate(term = rownames(ri), 
         signif = ifelse(ri$`Pr(>|t|)` < 0.05, T, F), 
         metric = "recycling intensity")


fit2 = lm(nodule.count.cv ~ as.factor(overlap) + as.factor(max_use_intensity) + as.factor(max_artifact_carry) +
            as.factor(max_flake_size) + as.factor(blank_prob) + as.factor(scavenge_prob) + as.factor(mu) + as.factor(num_agents) + 
            as.factor(size_preference) + as.factor(flake_preference) + as.factor(strict_selection) + as.factor(min_suitable_flake_size), 
          data = layer.var)
summary(fit2)
nc = as.data.frame(summary(fit2)$coefficients)
nc = nc %>% mutate(term = rownames(nc), 
