allsc = rbind(ri, cr, fc, nc, ne, nr, nd, ns)
allsc$metric = factor(allsc$metric, 
                      levels = c(
                        "recycling intensity", 
                        "cortex ratio", 
                        "flake count", "nodule count", 
                        "discard events", "scavenging events", 
                        "number of retouches", "number of encounters"
                      ))
allsc$low = allsc$Estimate - allsc$`Std. Error`
allsc$high = allsc$Estimate + allsc$`Std. Error`
allsc$positive = ifelse(allsc$Estimate > 0, "positive", "negative")

signif.labs = c("not significant", "significant")
names(signif.labs) = c("FALSE", "TRUE")

