plot2 = ggplot(allsc %>% filter(signif == T)) +
  geom_hline(yintercept = 0, color = I("red")) +
  #geom_linerange(aes(ymin = low, ymax = high, x = term, color = term)) +
  geom_point(aes(x = term, y = Estimate, shape = positive, color = term, fill = term))+
  coord_flip() +
  facet_wrap(~metric, ncol =2, strip.position = "right", scales = ) +
  scale_x_discrete(labels = x.labs) +
  scale_shape_manual(values = c(25, 24)) +
  scale_color_manual(values = c25) +
  scale_fill_manual(values = c25) +
  guides(color = "none", fill = "none") +
  labs(y = "estimate", shape = "coefficient direction") +
  theme(legend.position = "bottom", 
        strip.text = element_text(size = 7), 
        axis.text.y = element_text(size = 6), 
        axis.title.y = element_blank()) 
plot(plot2)


